// https://leetcode.com/problems/single-number-iii/

/**
 * @param {number[]} nums
 * @return {number[]}
 */
 var singleNumber = function(nums) {
  let diff = 0;
  let res = [0 , 0];
  // all duplicate number will get 0, so the final result is generated by two different numbers;
  for (let num of nums) {
    diff ^= num;
  }
  diff &= -diff;
  for (let num of nums) {
    if (num & diff) {
      res[0] ^= num;
    }
    else {
      res[1] ^= num;
    }
  }

  return res;
};